sp_widget.client_script:
function() {
  /* widget controller */
  var c = this;

 	c.closeAnnouncements = function() {
		c.data.showAnnouncement = false;
		c.data.action = "close_announcement";
		c.server.update();		
	};
}

sp_widget.css:
.announcement {
  background-color: white; 
  margin: 10px;
  padding-left: 10px;
  padding-bottom: 10px;
  padding-top: 10px;
}

.h2-space {
  margin: 10px;
  padding-left: 10px;
  padding-top: 10px;
}

.close-button {
  opacity: inherit;
  color: #4d6988;
  padding-top:2px;
