		a.sys_view_count = z.u_knowledge_record.sys_view_count.getDisplayValue();
		a.published = z.u_knowledge_record.published.getDisplayValue();
		a.text = z.u_knowledge_record.text.getDisplayValue();
		*/
		data.items.push(a);
	}
}
